tinymce.PluginManager.add('oppsmedia', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('oppsmedia', {
        text: false,
        icon: 'media',
        tooltip: 'Inserir Video do Sistema',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'Vídeos',
                url: '/admin/multimedias/video/?pop=oppseditor',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Fechar',
                    onclick: 'close'
                }]
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('oppsmedia', {
        text: 'Inserir Vídeo do Sistema',
        icon: false,
        context: 'insert',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'Vídeos',
                url: '/admin/multimedias/video/?pop=oppseditor',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Fechar',
                    onclick: 'close'
                }]
            });
        }
    });
});
